# -*- coding: utf-8 -*-
from mahjong.hand_calculating.yaku import Yaku


class Hana(Yaku):

    def __init__(self, yaku_id=None):
        super(Hana, self).__init__(yaku_id)

    def set_attributes(self):
        self.tenhou_id = 55

        self.name = 'Hana'
        self.english = 'hana'
        self.japanese = '花'

        self.han_open = 1
        self.han_closed = 1

        self.is_yakuman = False

    def is_condition_met(self, hand, *args):
        return True

    def __str__(self):
        return '花 {}'.format(self.han_closed)
