# -*- coding: utf-8 -*-
from functools import reduce

from mahjong.constants import HONOR_INDICES
from mahjong.hand_calculating.yaku import Yaku
from mahjong.utils import is_man


class Manhon(Yaku):

    def __init__(self, yaku_id=None):
        super(Manhon, self).__init__(yaku_id)

    def set_attributes(self):
        self.tenhou_id = 60

        self.name = 'Manzu Honitsu'
        self.english = 'Manzu Half Flush'
        self.japanese = '萬子混一色'

        self.han_open = 13
        self.han_closed = 13

        self.is_yakuman = True

    def is_condition_met(self, hand, *args):
        honor_sets = 0
        man_sets = 0
        for item in hand:
            if item[0] in HONOR_INDICES:
                honor_sets += 1
            elif is_man(item[0]):
                man_sets += 1
            else:
                return False

        return man_sets >= 1
